import React, {useRef} from 'react';
//import logo from './logo.svg';
import * as tf from "@tensorflow/tfjs";
import * as handpose from "@tensorflow-models/handpose";
import Webcam from "react-webcam";
import './App.css';
import {drawHand} from "./utilities";


function App() {

  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  const runHandpose = async () =>{
    const net = await handpose.load()
    console.log("Hand model Loaded.");
    // loop y detectar manos
    setInterval(() =>{
      detect(net)
    }, 100)

  };

  //detecta las manos al igual que configura el tamano de la camara y del canvas
  const detect = async (net) =>{
    //revisar si la info esta disponible
    if(
      typeof webcamRef.current !== "undefined" &&
      webcamRef.current !== null &&
      webcamRef.current.video.readyState === 4
    ) {
      //propiedades del video
      const video = webcamRef.current.video;
      const videoWidth = webcamRef.current.video.videoWidth;
      const videoHeight = webcamRef.current.video.videoHeight;

      //altura y  anchura de video
      webcamRef.current.width = videoWidth;
      webcamRef.current.height = videoHeight;

      //tamano de canvas
      canvasRef.current.width = videoWidth;
      canvasRef.current.height = videoHeight;

      //detecciones
      const hand = await net.estimateHands(video);
      console.log(hand);

      //dibujar
      const ctx = canvasRef.current.getContext("2d");
      drawHand(hand, ctx);
    }
  };



  runHandpose();

  return (
    <div className="App">
      <header className="App-header">
        <Webcam ref={webcamRef}
        style={{
          position: "absolute",
          marginLeft: "auto",
          marginRight: "auto",
          left: 0,
          right: 0,
          textAlign: "center",
          zindex:9,
          width: 640,
          height: 480
        }}/>

        <canvas
          ref={canvasRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zindex:9,
            width: 640,
            height: 480
          }}/>

      </header>
    </div>
  );
}

export default App;
